<div id="calculator">
	<div class="variants-block">
		<div class="variants-header row">
			<div style=" float: left; ">Choose Your Marketplace</div>
			<div style=" float: left; ">

				<select id="marketplace-select" class="form-select" aria-label="Default select example">
					<option value="Amazon.com">Amazon.com</option>
					<option value="Amazon.ca">Amazon.ca</option>
					<option value="Amazon.fr">Amazon.fr</option>
					<option value="Amazon.de">Amazon.de</option>
					<option value="Amazon.in">Amazon.in</option>
					<option value="Amazon.it">Amazon.it</option>
					<option value="Amazon.es">Amazon.es</option>
					<option value="Amazon.co.uk">Amazon.co.uk</option>
					<option value="Amazon.com.au">Amazon.com.au</option>
				</select>

			</div>
			<div class="clearfix"></div>
		</div>
		<div class="variants-body">
			<div class="variants-tabs">
				<div class="variants-tabs__tab "><img class="variants-tabs__tab__img"
						src="data:image/svg+xml;base64,"
						alt="">
					<div class="variants-tabs__tab__title">2-Step Super URL via ASIN</div>
					<div class="variants-tabs__tab__description">Send people to a search results page that only includes
						your product. Add keywords to the search so when someone buys your item it boosts your organic
						search rankings</div>
				</div>
			</div>
			<div class="variants-content">
				<div class="variant-content">
					<div class="variant-content__example">
					</div>
					<div class="variant-content__form">
						<div class="input-field">
							<span class="input-additional">(mandatory)</span>
							<input type="text" id="brand" placeholder="Brand" class="input" value="">
							<span class="input-error"></span>
						</div>
						<div class="input-field">
							<span class="input-additional">(mandatory)</span>
							<input type="text" id="keyword" placeholder="Keywords" class="input" value="">
							<span class="input-error"></span>
						</div>
						<div class="input-field">
							<span class="input-additional">(optional)</span>
							<input type="text" id="asin" placeholder="ASIN" class="input" value="">
							<span class="input-error"></span>
						</div>
						<button type="button" class="lg-bt lg-bt-form create-btn">Create Link</button>
					</div>
					<div class="row"><span class="status"></span></div>
					<div class="result">
						<textarea class="textarea" rows="3" readonly="true"></textarea>
						<span type="button" id="copy-btn" class="lg-bt lg-bt-form" disabled="">Copy</span>
						<span type="button" id="open-btn" class="lg-bt lg-bt-form" disabled="">Open</span>
					</div>
				</div>
			</div>
		</div>
	</div>
</div>